muclient.script:
local t_data = {}
function bx_ready_triggers() SetVariable("start_auc_line", 0) SetVariable("capture_start", 0)
EnableTrigger("auc_blank", true) end
function bx_disable_triggers() SetVariable("capture_start", 0) SetVariable("start_auc_line", 0)
EnableTrigger("auc_line", false) EnableTrigger("auc_body", false) EnableTrigger("auc_blank", false) end
function save_info(name, line, wildcards, styles) table.insert(t_data, styles) end
function hlink_auc_item(name, line, wildcards, styles) local cmd_line = ""
if (string.match(name, "lbid")) then cmd_line = "bxm " .. wildcards.num else cmd_line = "bxa " .. wildcards.num end
local is_done_checking_level = false local is_done_checking_num = false
for _, v in ipairs(styles) do local text_color = RGBColourToName(v.textcolour) local text_bgcolor = RGBColourToName(v.backcolour)
if (not is_done_checking_level) and (string.match(v.text, wildcards.level)) then ColourTell(text_color, text_bgcolor, v.text) is_done_checking_level = true elseif (not is_done_checking_num) and (string.match(v.text, wildcards.num)) then Hyperlink(cmd_line, v.text, "click the link or type " .. cmd_line, text_color, text_bgcolor, 0) is_done_checking_num = true else ColourTell(text_color, text_bgcolor, v.text) end end Note() end
function hlink_auc_list(name, line, wildcards, styles) local num = tonumber(wildcards.num) local cmd_line = ""
if (name == "bid_list") then cmd_line = "bxa " .. num elseif (name == "lbid_list") then cmd_line = "bxm " .. num else SetTriggerOption(name, "omit_from_output", "n") return end
local is_done_checking_bid_num = false
for _, v in ipairs(styles) do local text_color = RGBColourToName(v.textcolour) local text_bgcolor = RGBColourToName(v.backcolour)
if (not is_done_checking_bid_num) and (string.match(v.text, num)) then -- check for spaces before the actual bid num for w in string.gmatch(v.text, " ") do ColourTell("", "", w) end Hyperlink(cmd_line, num, "click the link or type " .. cmd_line, text_color, text_bgcolor, 0) is_done_checking_bid_num = true else ColourTell(text_color, text_bgcolor, v.text) end end Note() end
function bx_display_item_or_bid_amount(name, line, wildcards, styles) local cmd_line = (GetVariable("last_cmd"))
if (wildcards[1] ~= "") then cmd_line = cmd_line .. " " .. wildcards[1] end Execute(cmd_line) end
